package com.emailsender.controller;import com.emailsender.service.EmailService;import jakarta.mail.MessagingException;import model.EmailRequest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/api/mail")public class EmailController {    @Autowired    private EmailService emailService;    @PostMapping("/send-html")    public String sendHtmlMail(@RequestBody EmailRequest request) {        try {            emailService.sendHtmlMailWithAttachment(request);            return "HTML Mail sent successfully to " + request.getTo();        } catch (MessagingException e) {            return "Error while sending mail: " + e.getMessage();        }    }}