package com.emailsender.controller;import com.emailsender.service.NewsletterService;import jakarta.mail.MessagingException;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.*;import java.io.IOException;import java.util.List;@RestController@RequestMapping("/api/newsletter")public class NewsletterController {    @Autowired    private NewsletterService newsletterService;    @PostMapping("/send")    public String sendNewsletter(@RequestBody List<String> emails) {        try {            newsletterService.sendNewsletter(emails, "ðŸ“© Your Weekly Spring Boot Newsletter");            return "Newsletter sent to " + emails.size() + " subscribers.";        } catch (MessagingException | IOException e) {            return "Error sending newsletter: " + e.getMessage();        }    }}